using System.Collections;using System.Collections.Generic;using UnityEngine;[System.Serializable]public class PercentInfor {    public float startTime = 0;    public float percentageSum;    public float[] percentage;    public int GetChoosenNum() {        float random = Random.Range(0, percentageSum);        int i = 0;        for (; i < percentage.Length; i++) {            if (percentage[i] > random)                break;            random -= percentage[i];        }        return i;    }}[System.Serializable]public class PercenTime2Int {    [SerializeField ] PercentInfor[] percents = null;    int usedIdx = 0;    public PercenTime2Int() {    }    public int Time2Int(float inputTime)    {                if (usedIdx < percents.Length - 1 && inputTime > percents[usedIdx + 1].startTime)            usedIdx++;        return percents[usedIdx].GetChoosenNum();    }}